import React from 'react';
import styles from './MetricCard.module.css';

const MetricCard = ({ title, value, change, isPositive, chartSrc, onClick, isDropdownVisible }) => {
  return (
    <div className={styles.metricCard} onClick={onClick}>
      <h3>{title}</h3>
      <p>{value}</p>
      <p className={isPositive ? styles.positiveChange : styles.negativeChange}>{change}</p>
      <img src={chartSrc} alt={`${title} chart`} className={styles.chartImage} />

      {isDropdownVisible && (
        <div className={styles.dropdownBox}>
          {/* Add content for your dropdown here */}
          <p>Dropdown content for {title}</p>
        </div>
      )}
    </div>
  );
};

export default MetricCard;
