import React, { useState } from 'react';
import { Button } from 'antd'; // Assuming you are using Ant Design, as mentioned previously
import moment from 'moment';

const WeekNavigator = () => {
  const [currentDate, setCurrentDate] = useState(moment());

  const previousWeek = () => {
    setCurrentDate(currentDate.clone().subtract(7, 'days'));
  };

  const nextWeek = () => {
    setCurrentDate(currentDate.clone().add(7, 'days'));
  };

  const renderWeek = () => {
    const startOfWeek = currentDate.clone().startOf('isoWeek');
    const weekDays = [];
    for (let i = 0; i < 7; i++) {
      weekDays.push(startOfWeek.clone().add(i, 'days').format('DD MMMM, YYYY'));
    }
    return weekDays;
  };

  return (
    <div style={{ padding: '20px' }}>
      <div style={{ marginBottom: '20px' }}>
        {renderWeek().map((day, index) => (
          <div key={index}>{day}</div>
        ))}
      </div>
      <Button onClick={previousWeek} style={{ marginRight: '10px' }}>
        Previous Week
      </Button>
      <Button onClick={nextWeek}>Next Week</Button>
    </div>
  );
};

export default WeekNavigator;
